document.addEventListener("DOMContentLoaded",()=>{const t=document.querySelector(".portfolio-pages-section.lazy-section");if(!t)return;const e=t.querySelector(".figma-prototype");if(!e)return;const r=new IntersectionObserver(o=>{o.forEach(s=>{s.isIntersecting&&(e.dataset.src||(e.dataset.src=e.src,e.src=e.dataset.src),r.unobserve(t))})},{rootMargin:"300px 0px",threshold:.1});r.observe(t)});
